action.value:
DECLARE @dbcounter int;
SET @dbcounter = (select top 1 database_id from sys.databases where database_id > 4 order by database_id desc);
DECLARE @i int = 4;
DECLARE @query varchar(1000);
DECLARE @dbname varchar(100);
WHILE @i < @dbcounter
BEGIN
     SET @i = @i + 1;
     SET @query = (SELECT name from sys.databases where database_id=@i and database_id > 4);
     SET @dbname = (@query);
     SET @query = N'USE ' + @dbname + '; create table #sql_statements with (location=user_db, distribution=replicate)
as
( select row_number() over (order by a.name) sequence, (select db_name()) dbname, a.name tblname, ''DBCC PDW_SHOWPARTITIONSTATS (['' + (select db_name()) + ''.dbo.'' + a.name + ''])'' as statement from sys.tables a );
declare @nbr_statements int = (select count(*)  from #sql_statements);
declare @a int  = 1;
while(@a <= @nbr_statements)
begin
       declare @statement nvarchar(256)= (select statement from #sql_statements where sequence  = @a);
       select ''Current Database and Table Name: '' + dbname + ''.dbo.'' + tblname from #sql_statements where sequence  = @a;
       exec (@statement);
       set @a  += 1;
end
drop table #sql_statements'
EXEC (@query);
END

DECLARE @dbcounter int;
SET @dbcounter = (select top 1 database_id from sys.databases where database_id > 4 order by database_id desc);
DECLARE @i int = 4;
DECLARE @query varchar(1000);
DECLARE @dbname varchar(100);
WHILE @i < @dbcounter
BEGIN
     SET @i = @i + 1;
     SET @query = (SELECT name from sys.databases where database_id=@i and database_id > 4);
     SET @dbname = (@query);
     SET @query = N'USE ' + @dbname + '; create table #sql_statements with (location=user_db, distribution=replicate)
as
( select row_number() over (order by a.name) sequence, (select db_name()) dbname, a.name tblname, ''SELECT COUNT_BIG(*) AS TotalRows FROM '' + (select db_name()) + ''.dbo.'' + a.name as statement from sys.tables a );
declare @nbr_statements int = (select count(*)  from #sql_statements);
declare @a int  = 1;
while(@a <= @nbr_statements)
begin
       declare @statement nvarchar(256)= (select statement from #sql_statements where sequence  = @a)
       select ''Current Database and Table Name: '' + dbname + ''.dbo.'' + tblname from #sql_statements where sequence  = @a
       exec (@statement)
       set @a  += 1
end
drop table #sql_statements'
EXEC (@query);
END
